import styled from "styled-components";

const NewsBg = styled.section`
`

const NewsTop = styled.div`
  display: flex;
  position: relative;

  /*img{
    position: absolute;
    left: 0;
    top: 0;
  }*/
`

const NewsDown = styled.div`
  display: flex;
  position: relative;

  /*img{
    position: absolute;
    right: 0;
    top: 0;
  }*/
`

const NewsText = styled.div`
  height: 160px;
  overflow: hidden;

  p{
    font-family: 'Gotham';
    font-weight: normal;
    font-size: 15px;
    line-height: 23px;
  }
`

const NewsContent = styled.div`
  width: 50%;
`

const NewsImage = styled.div`
  position: absolute;
  left: 50%;
  top: 0;
  width: auto;
  min-height: 100%;
`

const News = () => {
    return(
        <NewsBg>
            <div className={'container'}>
                <NewsTop>
                    <NewsContent>
                        <span>Jan 4, 2016   |   In Culture</span>
                        <h2>Akita Inu Dog</h2>
                        <NewsText>
                            <p>
                                Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.
                                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec sed odio dui. Vivamus
                                sagittis lacus vel augue laoreet rutrum faucibus dolor auctor.
                            </p>
                            <p>
                                Donec ullamcorper nulla non metus auctor fringilla. Lorem ipsum dolor sit amet,
                                consectetur adipiscing elit. Sed posuere consectetur
                            </p>
                        </NewsText>
                    </NewsContent>
                    <NewsImage>
                        <img src={'./img/NewsTop.png'} alt={'News'} />
                    </NewsImage>
                </NewsTop>
                <NewsDown>
                    <NewsImage>
                    </NewsImage>
                    <NewsContent>
                    </NewsContent>
                </NewsDown>
            </div>
        </NewsBg>
    );
}

export default News;
