import styled from "styled-components";

const BgContact = styled.section`
  text-align: center;
  background-image: url("./img/Contact.png");
  background-size: cover;
  position: relative;
  padding-top: 100px;
`

const Heading = styled.h2`
  text-transform: uppercase;
  font-family: 'Gotham';
  font-weight: 500;
  font-size: 38px;
  color: #fff;
  margin: 50px 0 150px 0;
`

const C = styled.span`
  position: absolute;
  top: 0;
  left: 50%;
  transform: translateX(-50%);
  font-family: 'robotobold';
  font-size: 250px;
  color: #fff;
  opacity: 0.18;
`

const ContactData = styled.div`
  display: flex;

  input{
    width: 100%;
    font-family: 'Gotham';
    font-weight: normal;
    font-size: 15px;
    padding: 15px;
    border: 1px solid #fff;
    background: rgba(0,0,0,0);
    color: #fff;
  }

  input:nth-child(2){
    margin: 0 25px;
  }

  input::placeholder{
    color: #fff;
  }
`

const ContactMessage = styled.div`
  margin: 40px 0 50px 0;

  textarea{
    width: 100%;
    height: 150px;
    font-family: 'Gotham';
    font-weight: normal;
    font-size: 15px;
    padding: 15px;
    border: 1px solid #fff;
    background: rgba(0,0,0,0);
    color: #fff;
  }

  textarea::placeholder{
    color: #fff;
  }
`

const ContactButton = styled.div`
  margin-bottom: 100px;

  button{
    font-family: 'Gotham';
    font-weight: 500;
    font-size: 24px;
    color: #fff;
    background: #11749e;
    border: none;
    padding: 15px 60px;
  }
`

const Contact = () => {
    return(
        <BgContact>
            <div className={'container'}>
                <Heading>We’d love to hear about your project.</Heading>
                <C>C</C>
                <form>
                    <ContactData>
                        <input type={'text'} placeholder={'Name'} required />
                        <input type={'email'} placeholder={'Email'} required />
                        <input type={'tel'} placeholder={'Phone'} required />
                    </ContactData>
                    <ContactMessage>
                        <textarea placeholder={'Message'} required></textarea>
                    </ContactMessage>
                    <ContactButton>
                        <button type={'submit'}>SUBMIT</button>
                    </ContactButton>
                </form>
            </div>
            <div style="position:relative;overflow:hidden;"><a
                href="https://yandex.by/maps/26008/rechitsa/?utm_medium=mapframe&utm_source=maps"
                style="color:#eee;font-size:12px;position:absolute;top:0px;">Речица</a><a
                href="https://yandex.by/maps/26008/rechitsa/?ll=30.395278%2C52.359818&utm_medium=mapframe&utm_source=maps&z=12"
                style="color:#eee;font-size:12px;position:absolute;top:14px;">Карта Речицы с улицами и номерами домов
                онлайн — Яндекс.Карты</a>
                <iframe src="https://yandex.by/map-widget/v1/-/CCUquHT0xC" width="560" height="400" frameBorder="1"
                        allowFullScreen="true" style="position:relative;"></iframe>
            </div>
        </BgContact>
    );
}

export default Contact;
