import {Column} from "../index";
import {connect} from "react-redux";
import styled from "styled-components";
import React, {Fragment} from "react";
import columnsActions from '../actions/columns';
import cardsActions from '../actions/cards';
import { DragDropContext } from "react-beautiful-dnd";

const oneState = [
    {
        title: "План на месяц",
        cards: [
            "Пройти курс по React",
            "Отметить день рождения",
            "Записаться на курсы английского языка, чтобы уехать жить в Лондон",
            "Пройти курс по React",
            "Отметить день рождения",
            "Записаться на курсы английского языка, чтобы уехать жить в Лондон",
            "Пройти курс по React",
            "Отметить день рождения",
            "Записаться на курсы английского языка, чтобы уехать жить в Лондон",
            "Пройти курс по React",
            "Отметить день рождения",
            "Записаться на курсы английского языка, чтобы уехать жить в Лондон"
        ]
    },
    {
        title: "План на день",
        cards: [
            "Записаться на курс по React",
            "Забронировать тир на субботу",
            "Накидать тем для статей в блог",
            "Пройти курс по React",
            "Отметить день рождения",
            "Записаться на курсы английского языка, чтобы уехать жить в Лондон"
        ]
    },
    {
        title: "План на день",
        cards: [
            "Пройти курс по React",
            "Отметить день рождения",
            "Записаться на курсы английского языка, чтобы уехать жить в Лондон",
            "Записаться на курс по React",
            "Забронировать тир на субботу",
            "Накидать тем для статей в блог"
        ]
    },
    {
        title: "План на день",
        cards: [
            "Записаться на курс по React",
            "Забронировать тир на субботу",
            "Накидать тем для статей в блог"
        ]
    }
];

const Columns = ({items, addColumn, addCard, removeColumn, reorderCards}) => {
    const onDragEnd = result => {
        const { source, destination } = result;
        if (
            !destination ||
            (source.droppableId === destination.droppableId &&
                source.index === destination.index)
        ) {
            return;
        }
        reorderCards({
            source,
            destination
        });
    };

    return (
        <Fragment>
            <DragDropContext onDragEnd={onDragEnd}>
                {items.map((item, index) => (
                    <Column
                        {...item}
                        key={index}
                        columnIndex={index}
                        onAddColumn={addColumn}
                        onAddCard={addCard}
                        onRemove={removeColumn}
                        onReorder={reorderCards}
                    />
                ))}
            </DragDropContext>
            <Column onAddColumn={addColumn} onAddCard={addCard} />
        </Fragment>
    )
};

export default connect(
    ({columns}) => ({items: columns}),
    {...columnsActions, ...cardsActions}
)(Columns);
