import React, {Fragment, useState} from 'react';
import {Button, Card} from "./index";
import styled from "styled-components";

const AddFormContent = styled.div`
`

const AddFormInput = styled.div`
  textarea{
    border: 0;
    padding: 0;
    margin: 0;
    width: 100%;
    resize: none;
    outline: none;
  }
`

const AddFormBottom = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`

const AddFormClear = styled.img`
  cursor: pointer;
`

const PanelBottom = styled.div`


  :hover{
    background-color: rgba(0,0,0,0.1);
  }
`

const PanelButton = styled.div`
  border-radius: 0 0 3px 3px;
  padding: 12px;
  display: flex;
  align-items: center;
  cursor: pointer;
  color: #6B808C;

  :hover {
    color: #323b41;
  }

  img {
    margin-right: 8px;
  }
`

const AddForm = ({ children }) => {
    const [ showForm, setShowForm ] = useState(false)

    return (
        <Fragment>
            {showForm ? (
                <AddFormContent>
                <AddFormInput>
                    <Card>
                        <textarea rows={'3'} />
                    </Card>
                    <AddFormBottom>
                        <Button>Добавить карточку</Button>
                        <AddFormClear src={'./img/clear.svg'} alt={'clear'} />
                    </AddFormBottom>
                </AddFormInput>
            </AddFormContent>
                ) : (
            <PanelBottom>
                <PanelButton>
                    <img src={'./img/add.svg'} alt={'Add icon'} />
                    <span>Добавить еще одну карточку</span>
                </PanelButton>
            </PanelBottom>
            )}
        </Fragment>
    );
};

export default AddForm;
