import React, {Fragment} from 'react';
import {Button, Card} from "./index";
import styled from "styled-components";

const AddFormContent = styled.div`
`

const AddFormInput = styled.div`
`

const AddFormBottom = styled.div`
`

const PanelBottom = styled.div`


  :hover{
    background-color: rgba(0,0,0,0.1);
  }
`

const PanelButton = styled.div`
  border-radius: 0 0 3px 3px;
  padding: 12px;
  display: flex;
  align-items: center;
  cursor: pointer;
  color: #6B808C;

  :hover {
    color: #323b41;
  }

  img {
    margin-right: 8px;
  }
`

const AddForm = ({ children }) => {
    return (
        <Fragment>
            <AddFormContent>
                <AddFormInput>
                    <Card><textarea rows={'3'}></textarea></Card>
                    <AddFormBottom>
                        <Button>Добавить карточку</Button>
                        <img src={'./img/clear.svg'} alt={'clear'} />
                    </AddFormBottom>
                </AddFormInput>
                <PanelBottom>
                    <PanelButton>
                        <img src={'./img/add.svg'} alt={'Add icon'} />
                        <span>Добавить еще одну карточку</span>
                    </PanelButton>
                </PanelBottom>
            </AddFormContent>
        </Fragment>
    );
};

export default AddForm;
