import React from 'react';
import styled from "styled-components";
import $ from "jquery";
import { Columns } from "../Card/containers";

const Main = styled.section`
  background: #222;
  padding-top: 109px;

  span{
    font-family: 'Gotham';
    font-weight: normal;
    font-size: 13px;
  }
  h2{
    font-family: 'Gotham';
    font-weight: 500;
    font-size: 25px;
    text-transform: uppercase;
    margin: 15px 0 10px 0;
  }
  p{
    font-family: 'Gotham';
    font-weight: normal;
    font-size: 15px;
    line-height: 23px;
    margin-top: 20px;
  }

  @media (max-width: 991px){
    h2{
      margin: 10px 0 5px 0;
    }
  }
  @media (max-width: 767px){
    span{
      font-size: 12px;
    }
    h2{
      font-size: 22px;
    }
    p{
      font-size: 14px;
      line-height: 20px;
    }
  }
`

const SvImage = styled.div`
  :nth-child(even) {
    position: absolute;
    left: 50%;
    top: 0;
    //background-image: url("./img/NewsTop.png");
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
    height: 100%;
    width: 50%;
  }
  :nth-child(odd) {
    position: absolute;
    left: 0;
    top: 0;
    //background-image: url("./img/NewsTop.png");
    background-size: cover;
    background-repeat: no-repeat;
    background-position: center;
    height: 100%;
    width: 50%;
  }

  @media (max-width: 500px){
    left: 0 !important;
    width: 100% !important;
    opacity: 0.3;
    z-index: 1;  
  }
`

const SvContainer = styled.div`
  position: relative;

  :nth-child(odd){
    background: #222;
  }
  :nth-child(even){
    background: #f3f3f3;
  }
`

const SvText = styled.div`
  height: 200px;
  overflow: hidden;
`

const SvTopContent = styled.div`
  width: 50%;
  padding: 80px 100px 120px 0;
  color: #fff;

  @media (max-width: 991px){
    padding: 40px 70px 70px 0;
  }
  @media (max-width: 767px){
    padding: 30px 30px 30px 0;
  }
  @media (max-width: 500px){
    position: relative;
    z-index: 2;
    width: 100%;
    text-shadow: 0 0 1px rgba(255, 255, 255, 1);
    padding: 30px;
  }
`

const SvDownContent = styled.div`
  width: 50%;
  padding: 80px 0 120px 100px;
  color: #222;
  margin-left: 50%;

  @media (max-width: 991px){
    padding: 40px 0 70px 70px;
  }
  @media (max-width: 767px){
    padding: 30px 0 30px 30px;
  }
  @media (max-width: 500px){
    position: relative;
    z-index: 2;
    width: 100%;
    text-shadow: 0 0 0px rgba(0, 0, 0, 1);
    margin-left: 0;
    padding: 30px;
  }
`

const ColumnContent = styled.div`
  display: flex;
  justify-content: center;
  padding: 150px 0 20px 0;
  text-align: center;

  > div:nth-child(2){
    position: relative;
    top: -100px;
    margin: 0 70px;
  }

  @media (max-width: 991px){
    > div:nth-child(2){
      margin: 0 30px;
    }
  }
  @media (max-width: 767px) {
    padding: 100px 0 20px 0;

    > div:nth-child(2) {
      margin: 0;
      top: -70px;
    }
  }
`

const ColumnHeader = styled.div`
  h2{
    color: #fff;
    text-align: center;
    margin-top: 50px;
    font-family: 'Gotham';
    font-weight: 500;
    font-size: 35px;
  }
  @media (max-width: 767px){
    h2{
      font-size: 30px;
      margin-top: 30px;
    }
  }
  @media (max-width: 767px){
    h2{
      font-size: 25px;
      margin-top: 30px;
    }
  }
`

