import styled from "styled-components";

const NewsBg = styled.section`
  background: rgb(243,243,243);
  background: linear-gradient(0deg, rgba(243,243,243,1) 0%, rgba(243,243,243,1) 50%, rgba(34,34,34,1) 50%, rgba(34,34,34,1) 100%);

  span{
    font-family: 'Gotham';
    font-weight: normal;
    font-size: 13px;
  }
  h2{
    font-family: 'Gotham';
    font-weight: 500;
    font-size: 25px;
    text-transform: uppercase;
    margin: 15px 0 10px 0;
  }
  p{
    font-family: 'Gotham';
    font-weight: normal;
    font-size: 15px;
    line-height: 23px;
    margin-top: 20px;
  }
`

const NewsContainer = styled.div`
  display: flex;
  position: relative;
`

const NewsImage = styled.div`
  width: 50%;
  position: relative;

  img{
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
  }
`

const NewsText = styled.div`
  height: 160px;
  overflow: hidden;
`

const NewsTopContent = styled.div`
  width: 50%;
  padding: 80px 100px 120px 0;
  color: #fff;
`

const NewsDownContent = styled.div`
  width: 50%;
  padding: 80px 0 120px 100px;
  color: #222;
  margin-left: 50%;
`

const NewsTopImage = styled.div`
  position: absolute;
  left: 0;
  top: 0;
  background-image: url("./img/NewsTop.png");
  background-size: cover;
  background-repeat: no-repeat;
  background-position: center;
  min-height: 100%;
  min-width: 100%;
`

const NewsDownImage = styled.div`
  position: absolute;
  top: 1px;
  left: 1px;
  background-image: url("./img/NewsDown.png");
  background-size: auto;
  background-repeat: no-repeat;
  background-position: center;
  height: 100%;
  width: 100%;
`

const News = () => {
    return(
        <NewsBg>
            <div className={'container'}>
                <NewsContainer>
                    <NewsTopContent>
                        <span>Jan 4, 2016   |   In Culture</span>
                        <h2>Akita Inu Dog</h2>
                        <NewsText>
                            <p>
                                Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.
                                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec sed odio dui. Vivamus
                                sagittis lacus vel augue laoreet rutrum faucibus dolor auctor.
                            </p>
                            <p>
                                Donec ullamcorper nulla non metus auctor fringilla. Lorem ipsum dolor sit amet,
                                consectetur adipiscing elit. Sed posuere consectetur
                            </p>
                        </NewsText>
                    </NewsTopContent>
                    <NewsImage>
                        {/*<NewsTopImage />*/}
                        <img src={'./img/NewsTop.png'} alt={'News'} />
                    </NewsImage>
                </NewsContainer>
                <NewsContainer>
                    {/*<NewsImage>
                        <NewsDownImage />
                    </NewsImage>*/}
                    <NewsDownImage />
                    <NewsDownContent>
                        <span>Jan 4, 2016   |   In Culture</span>
                        <h2>Akita Inu Dog</h2>
                        <NewsText>
                            <p>
                                Cum sociis natoque penatibus et magnis dis parturient montes, nascetur ridiculus mus.
                                Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec sed odio dui. Vivamus
                                sagittis lacus vel augue laoreet rutrum faucibus dolor auctor.
                            </p>
                            <p>
                                Donec ullamcorper nulla non metus auctor fringilla. Lorem ipsum dolor sit amet,
                                consectetur adipiscing elit. Sed posuere consectetur
                            </p>
                        </NewsText>
                    </NewsDownContent>
                </NewsContainer>
            </div>
        </NewsBg>
    );
}

export default News;
