import styled from "styled-components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./FontAwesome";
import '../fonts/stylesheet.css';

const Row = styled.div`
  display: flex;
  align-items: center;

  #menu-bar{
    display: none;
    color: #fff;
    cursor: pointer;
  }

  @media (max-width: 767px){
    justify-content: space-between;

    #menu-bar{
      display: block;
      font-size: 50px;
    }
  }
`
const Head = styled.header`
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  padding: 15px 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.3);
`
const Logo = styled.div`
  width: 30%;
  text-align: center;

  img{
    cursor: pointer;
  }

  @media (max-width: 991px){
    width: 15%;
  }
  @media (max-width: 767px){
    width: 70%;
    text-align: left;
  }
`
const MainMenu = styled.div`
  width: 50%;
  padding-right: 80px;

  ul{
    display: flex;
    list-style: none;
    justify-content: space-evenly;
  }
  ul li a{
    color: #fff;
    text-decoration: none;
    cursor: pointer;
  }
  ul li:hover a{
    border-bottom: 5px solid #11749e;
    color: #1698D0;
    padding-bottom: 40px;
  }
  ul li a.active{
    border-bottom: 5px solid #11749e;
    padding-bottom: 40px;
  }

  @media (max-width: 991px){
    width: 70%;
    padding-right: 0;
  }
  @media (max-width: 767px){
    //display: none;
    position: fixed;
    left: 0;
    right: 0;
    top: 0;
    background: #222;
    z-index: 10;
    width: 100%;
    height: 100%;

    ul{
      position: relative;
      top: 50%;
      left: 25%;
      transform: translateY(-50%);
      flex-direction: column;
      text-align: center;
    }
    ul li{
      //border-bottom: 1px solid #11749e;
      cursor: pointer;
      width: 75%;
      margin: 10px 0;
      background: rgb(17,116,158);
      background: linear-gradient(90deg, rgba(17,116,158,0) 0%, rgba(17,116,158,1) 50%, rgba(17,116,158,0) 100%);
    }
    ul li:first-child{
      //border-top: 1px solid #11749e;
      //border-radius: 50px;
    }
    ul li:hover{
      //background: #11749e;
    }
    ul li a{
      padding: 20px 10px;
      display: inline-block;
      width: 100%;
      height: 100%;
    }
    ul li a.active{
      border-bottom: none;
      padding-bottom: 20px;
    }
    ul li:hover a{
      border-bottom: none;
      padding-bottom: 20px;
      color: #fff;
    }
  }
`
const Contacts = styled.div`
  width: 20%;

  svg{
    margin: 0 8px;
    cursor: pointer;
    color: #fff;
  }
  svg:hover{
    color: #1698D0;
  }

  @media (max-width: 991px){
    width: 15%;

    svg{
      margin: 0 5px;
    }
  }
  @media (max-width: 767px){
    display: none;
  }
`

const Header = () => {
    return(
        <Head>
            <div className='container'>
                <Row>
                    <Logo>
                        <a href='/'><img src='./img/logo.png' alt='CS' /></a>
                    </Logo>
                    <MainMenu>
                        <ul>
                            <li><a href='/' className='active'>Home</a></li>
                            <li><a href='/'>Services</a></li>
                            <li><a href='/'>Our work</a></li>
                            <li><a href='/'>Reviews</a></li>
                            <li><a href='/'>Contact us</a></li>
                        </ul>
                    </MainMenu>
                    <Contacts>
                        <a href={'https://facebook.com'} target={'_blank'}><FontAwesomeIcon icon={['fab', 'facebook']} /></a>
                        <a href={'https://twitter.com'} target={'_blank'}><FontAwesomeIcon icon={['fab', 'twitter']} /></a>
                        <a href={'https://skype.com'} target={'_blank'}><FontAwesomeIcon icon={['fab', 'skype']} /></a>
                        <a href={'https://instagram.com'} target={'_blank'}><FontAwesomeIcon icon={['fab', 'instagram']} /></a>
                    </Contacts>
                    <FontAwesomeIcon id={'menu-bar'} icon={['fas', 'bars']} />
                </Row>
            </div>

        </Head>
    );
}

export default Header;
