import {Card, Column} from "../index";
import {connect} from "react-redux";
import styled from "styled-components";
import React, {Fragment} from "react";
import columnsActions from '../actions/columns';
import cardsActions from '../actions/cards';
import { DragDropContext, Droppable } from "react-beautiful-dnd";

const ColumnsItems = styled.div`
  display: flex;
  align-items: flex-start;
`

const Columns = ({items, addColumn, addCard, removeColumn, reorderCards}) => {
    const onDragEnd = ({ source, destination }) => {

        if (
            !destination ||
            source.droppableId === destination.droppableId &&
            source.index === destination.index
        ) {
            return;
        }

        // onReorder({
        //     columnIndex,
        //     sourceIndex: source.index,
        //     destinationIndex: destination.index
        // });
    }

    return (
        <Fragment>
            <DragDropContext onDragEnd={onDragEnd}>
                {items.map((item, index) => (
                    <Droppable type='COLUMN' droppableId='board'>
                        {(provided) => (
                            <ColumnsItems {...provided.droppableProps} ref={provided.innerRef}>
                                <Column
                                    {...item}
                                    key={index}
                                    columnIndex={index}
                                    onAddColumn={addColumn}
                                    onAddCard={addCard}
                                    onRemove={removeColumn}
                                    onReorder={reorderCards}
                                />
                            </ColumnsItems>
                        )}
                    </Droppable>
                ))}
                <Column onAddColumn={addColumn} onAddCard={addCard} />
            </DragDropContext>
        </Fragment>
    )
};

export default connect(
    ({columns}) => ({items: columns}),
    {...columnsActions, ...cardsActions}
)(Columns);
