const initialState = {
    items: [
        {
            title: 'План на месяц',
            cards: [
                {text: 'Пройти курс по React'},
                {text: 'Отметить день рождения'},
                {text: 'Записаться на курсы английского языка, чтобы уехать жить в Лондон'}
            ]
        }
    ]
};

export default (state = initialState, action) => {
    switch (action.type) {
        case 'CARDS:ADD':
            return {
                ...state,
                items: action.payload
            };
        default:
            return state;
    }
};
