import React, {Fragment, useState} from 'react';
import {Button, Card} from "./index";
import styled from "styled-components";

const AddFormContent = styled.div`
`

const AddFormInput = styled.div`
  textarea{
    border: 0;
    padding: 0;
    margin: 0;
    width: 100%;
    resize: none;
    outline: none;
  }
`

const AddFormBottom = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`

const AddFormClear = styled.img`
  cursor: pointer;
`

const AddForm = ({ children }) => {
    const [ showForm, setShowForm ] = useState()

    return (
        <Fragment>
            <AddFormContent>
                <AddFormInput>
                    <Card>
                        <textarea rows={'3'} />
                    </Card>
                    <AddFormBottom>
                        <Button>Добавить карточку</Button>
                        <AddFormClear src={'./img/clear.svg'} alt={'clear'} />
                    </AddFormBottom>
                </AddFormInput>
            </AddFormContent>
        </Fragment>
    );
};

export default AddForm;
