import React from 'react';
import styled from "styled-components";
import $ from "jquery";


let comments = [];

const Main = styled.div`
  padding-top: 209px;
  background: #bbb;
`

const ShowComments = () => {
    let commentField = document.getElementById('comment-field');
    let out = '';
    comments.forEach(function(item){
        out += `<p class="text-right small"><em>${TimeConverter(item.time)}</em></p>`;
        out += `<p class="alert alert-primary" role="alert">${item.name}</p>`;
        out += `<p class="alert alert-success" role="alert">${item.body}</p>`;
    });
    commentField.innerHTML = out;
}

const SaveComments = () => {
    localStorage.setItem('comments', JSON.stringify(comments));
}

const handleFormSubmit = () => {
    const { user, rememberMe } = this.state;
    localStorage.setItem('rememberMe', rememberMe);
    localStorage.setItem('user', rememberMe ? user : '');
};

const LoadComments = () => {
    if (localStorage.getItem('comments')) comments = JSON.parse(localStorage.getItem('comments'));
    ShowComments();
}

const TimeConverter = (UNIX_timestamp) => {
    let a = new Date(UNIX_timestamp * 1000);
    let months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
    let year = a.getFullYear();
    let month = months[a.getMonth()];
    let date = a.getDate();
    let hour = a.getHours();
    let min = a.getMinutes();
    let sec = a.getSeconds();
    let time = date + ' ' + month + ' ' + year + ' ' + hour + ':' + min + ':' + sec ;
    return time;
}

const Comments = () => {
    let commentName = document.getElementById('comment-name');
    let commentBody = document.getElementById('comment-body');

    let comment = {
        name : commentName.value,
        body : commentBody.value,
        time : Math.floor(Date.now() / 1000)
    }

    commentName.value = '';
    commentBody.value = '';

    comments.push(comment);
    SaveComments();
    ShowComments();
}

const Reviews = () => {
    $('.active').removeClass('active');
    $('#reviews').addClass('active');
    // LoadComments();

    return (
        <Main>
            <h2 className="text-center">
                Comment
            </h2>
            <div id="comment-field"></div>
            <form>
                <div className="form-group">
                    <label htmlFor="comment-name">Name:</label>
                    <input type="text" className="form-control" id="comment-name" value={this.state.user} onChange={this.handleChange} placeholder="Enter your name" />
                </div>
                <div className="form-group">
                    <label htmlFor="comment-body">Comment:</label>
                    <textarea type="password" className="form-control" id="comment-body" value={this.state.user} onChange={this.handleChange} placeholder="comment"></textarea>
                </div>
                <div className="form-group form-check text-right">
                    <button type="submit" id="comment-add" className="btn btn-primary">add Comment</button>
                </div>
            </form>
        </Main>
    );
};

export default Reviews;
