import styled from "styled-components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "../FontAwesome";
import '../../fonts/stylesheet.css';
import $ from 'jquery';

const BgImage = styled.div`
  display: flex;
  background-image: url("./img/bgheader.png");
  background-repeat: no-repeat;
  background-size: cover;
  background-attachment: fixed;
  height: 880px;
  align-items: center;
`

const BgContent = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
  top: 30px;

  h1{
    font-size: 150px;
    font-family: 'Gotham';
    font-weight: 700;
    color: #fff;
    margin: 0 auto;
  }
  h2{
    font-size: 25px;
    font-family: 'Gotham';
    font-weight: 400;
    color: #11749e;
    letter-spacing: 5px;
  }
  div{
    display: flex;
    position: relative;
    flex-direction: column;
    align-items: center;
    top: 90px;
    color: #fff;
  }
  span{
    font-size: 13px;
    margin-bottom: 15px;
  }
  svg{
    cursor: pointer;
  }
  svg:hover{
    color: #11749e;
  }
  a{
    text-decoration: none;
  }
  a:visited{
    color: #fff;
  }
`

const $page = $('html, body');
$(document).ready(function (){

})
$('a[href*="#"]').click(function() {
    $page.animate({
        scrollTop: $($.attr(this, 'href')).offset().top
    }, 400);
    return false;
});

const BgHeader = () => {
    return(
        <BgImage>
            <div className='container'>
                <BgContent>
                    <h1>CREATIVES</h1>
                    <h2>POWERED BY HOMERION</h2>
                    <div>
                        <span>Scroll Down</span>
                        <a href={'#about'}><FontAwesomeIcon icon={['fas', 'chevron-circle-down']} size='3x' /></a>
                    </div>
                </BgContent>
            </div>
        </BgImage>
    );
}


export default BgHeader;
