import React from 'react';
import styled from "styled-components";
import $ from "jquery";


let comments = [];

const Main = styled.div`
  padding-top: 109px;
  background: #bbb;
  min-height: 100%;
`

const RevHead = styled.h2`
  text-align: center;
  margin-top: 30px;
  font-size: 30px;
  font-family: 'Gotham';
  font-weight: 500;
  text-transform: uppercase;
`

const RevDesc = styled.h2`
  text-align: center;
  margin: 20px 0;
  font-size: 22px;
  font-family: 'Gotham';
  font-weight: normal;
  text-transform: uppercase;
`

const RevComments = styled.div`
`

const RevForm = styled.form`
  text-align: center;
  margin-bottom: 50px;
`

const FormName = styled.div`
  input{
    width: 50%;
    font-family: 'Gotham';
    font-weight: normal;
    font-size: 15px;
    padding: 10px 15px;
    border: 1px solid #fff;
    background: rgba(0,0,0,0);
    color: #fff;
  }

  input:focus{
    outline: none;
  }

  input::placeholder{
    color: #fff;
  }
`

const FormComment = styled.div`
  margin: 15px 0;

  textarea{
    width: 50%;
    height: 150px;
    font-family: 'Gotham';
    font-weight: normal;
    font-size: 15px;
    padding: 15px;
    border: 1px solid #fff;
    background: rgba(0,0,0,0);
    color: #fff;
    resize: none;
  }

  textarea::placeholder{
    color: #fff;
  }

  textarea:focus{
    outline: none;
  }
`

const FormBtn = styled.div`
  button {
    font-family: 'Gotham';
    font-weight: 500;
    font-size: 18px;
    color: #fff;
    background: #11749e;
    border: none;
    padding: 12px 40px;
    cursor: pointer;
  }

  button:focus {
    background: #1999d0;
    -webkit-box-shadow: 0 0 15px 5px rgba(255, 255, 255, 0.2);
    -moz-box-shadow: 0 0 15px 5px rgba(255, 255, 255, 0.2);
    box-shadow: 0 0 15px 5px rgba(255, 255, 255, 0.2);
  }
`

const Show = (data) => {
    $('#comment-field').html(data);
}

const ShowComments = () => {
    let out = '';
    comments.forEach(function(item){
        out += `<p class="text-right small"><i>${TimeConverter(item.time)}</i></p>`;
        out += `<p class="alert alert-primary" role="alert">${item.name}</p>`;
        out += `<p class="alert alert-success" role="alert">${item.body}</p>`;
    });
    setTimeout(function (){Show(out)}, 1);
}

const SaveComments = () => {
    localStorage.setItem('comments', JSON.stringify(comments));
}

const LoadComments = () => {
    if (localStorage.getItem('comments')) comments = JSON.parse(localStorage.getItem('comments'));
    ShowComments();
}

const TimeConverter = (UNIX_timestamp) => {
    let a = new Date(UNIX_timestamp * 1000);
    let months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
    let year = a.getFullYear();
    let month = months[a.getMonth()];
    let date = a.getDate();
    let hour = a.getHours();
    let min = a.getMinutes();
    let sec = a.getSeconds();
    let time = date + ' ' + month + ' ' + year + ' ' + hour + ':' + min + ':' + sec ;
    return time;
}

const Comments = (event) => {
    event.preventDefault();

    if ($('#comment-name').val() != '' && $('#comment-body').val() != ''){
        let comment = {
            name: $('#comment-name').val(),
            body: $('#comment-body').val(),
            time : Math.floor(Date.now() / 1000)
        }

        $('#comment-name').val('');
        $('#comment-body').val('');

        comments.push(comment);
        SaveComments();
        ShowComments();
    }
}

const Reviews = () => {
    $('.active').removeClass('active');
    $('#reviews').addClass('active');
    LoadComments();

    return (
        <Main>
            <div className={'container'}>
                <RevHead>Customer Reviews</RevHead>
                <RevDesc>Leave your review</RevDesc>
                <RevForm>
                    <FormName>
                        <input type={"text"} id={"comment-name"} placeholder={"Enter your name"} minLength={'10'} required/>
                    </FormName>
                    <FormComment>
                        <textarea type={"text"} id={"comment-body"} placeholder={"Comment"} required></textarea>
                    </FormComment>
                    <FormBtn>
                        <button type={"submit"} id={"comment-add"} onClick={Comments}>Add a review</button>
                    </FormBtn>
                </RevForm>
                <RevComments id={"comment-field"}></RevComments>
            </div>
        </Main>
    );
};

export default Reviews;
