import React from 'react';
import styled from "styled-components";
import PropTypes from 'prop-types';
import { Card, Button } from "./index";
import addSvg from './add.svg';

const PanelContent = styled.div`
  background: #DFE3E6;
  border-radius: 3px;
  width: 272px;
`

const PanelItems = styled.div`
  padding: 12px 12px 0 12px;
`

const PanelBottom = styled.div`


  :hover{
    background-color: rgba(0,0,0,0.1);
  }
`

const PanelButton = styled.div`
  border-radius: 0 0 3px 3px;
  padding: 12px;
  display: flex;
  align-items: center;
  cursor: pointer;
  color: #6B808C;

  :hover {
    color: #323b41;
  }

  img {
    margin-right: 8px;
  }
`

const AddFormContent = styled.div`
`

const AddFormInput = styled.div`
`

const AddFormBottom = styled.div`
`

const AddForm = () => {
    return(
        <AddFormContent>
            <AddFormInput>
                <Card><textarea rows={'3'}></textarea></Card>
                <AddFormBottom>
                    <Button>Добавить карточку</Button>
                </AddFormBottom>
            </AddFormInput>
        </AddFormContent>
    );
}

const Panel = ({ items }) => {
    return (
        <PanelContent>
            <PanelItems>
                {items.map(card => (
                    <Card {...card} />
                ))}
            </PanelItems>
            <PanelBottom>
                <PanelButton>
                    <img src={'./img/add.svg'} alt={'Add icon'} />
                    <span>Добавить еще одну карточку</span>
                </PanelButton>
            </PanelBottom>
        </PanelContent>
    );
};

export default Panel;
