import React, {Fragment} from 'react';
import styled from "styled-components";
import PropTypes from 'prop-types';
import { Card, AddForm } from "./index";
import { DragDropContext, Droppable } from "react-beautiful-dnd";
import classNames from 'classnames';

const ColumnContent = styled.div`
  background: #DFE3E6;
  border-radius: 3px;
  width: 272px;
  margin-right: 12px;
  overflow: hidden;
`

const ColumnItems = styled.div`
  padding: 12px 12px 0 12px;
`

const ColumnTitle = styled.div`
  padding: 12px 12px 0 12px;
  display: flex;
  justify-content: space-between;
`

const ColumnRemove = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 3px;
  width: 24px;
  height: 24px;
  cursor: pointer;

  img{
    width: 12px;
  }
  :hover{
    background-color: rgba(9, 30, 66, 0.13);
  }
`

const Column = ({ columnIndex, title, cards, onAddColumn, onAddCard, onRemove, onReorder }) => {
    const removeColumn = () => {
        if (global.confirm('Вы действительно хотите удалить колонку?')) {
            onRemove(columnIndex);
        }
    };

    const onDragEnd = result => {
        onReorder();
    }

    return (
        <ColumnContent className={classNames('', {'column-empty': !cards})}>
            {title && (
                <ColumnTitle>
                    <b>{title}</b>
                    <ColumnRemove onClick={removeColumn}>
                        <img src={'./img/clear.svg'} alt={'remove column'} />
                    </ColumnRemove>
                </ColumnTitle>
            )}
            {cards && (
                <ColumnItems>
                    <DragDropContext onDragEnd={this.onDragEnd}>
                        <Droppable droppableId="droppable">
                            {(provided, snapshot) => (
                                <div
                                    {...provided.droppableProps}
                                    ref={provided.innerRef}
                                    // style={getListStyle(snapshot.isDraggingOver)}
                                >
                                    {cards.map((card, index) => (
                                        <Card key={index} cardIndex={index}>{card}</Card>
                                    ))}
                                    {provided.placeholder}
                                </div>
                            )}
                        </Droppable>
                    </DragDropContext>
                </ColumnItems>
            )}
            <AddForm
                columnIndex={columnIndex}
                isEmptyColumn={!cards}
                onAddColumn={onAddColumn}
                onAddCard={onAddCard}
            />
        </ColumnContent>
    );
};

Column.propTypes = {
    cards: PropTypes.node,
    title: PropTypes.string
};

export default Column;
