import React from 'react';
import styled from "styled-components";
import $ from "jquery";


let comments = [];

const Main = styled.div`
  padding-top: 109px;
  background: #bbb;
  min-height: 100%;
`

const RevHead = styled.h2`
  text-align: center;
  margin: 50px 0 0 0;
  font-size: 30px;
  font-family: 'Gotham';
  font-weight: 500;
`

const RevComments = styled.div`
`

const RevForm = styled.form`
`

const FormName = styled.div`
`

const FormComment = styled.div`
`

const FormBtn = styled.div`
`

const Show = (data) => {
    $('#comment-field').html(data);
}

const ShowComments = () => {
    let out = '';
    comments.forEach(function(item){
        out += `<p class="text-right small"><i>${TimeConverter(item.time)}</i></p>`;
        out += `<p class="alert alert-primary" role="alert">${item.name}</p>`;
        out += `<p class="alert alert-success" role="alert">${item.body}</p>`;
    });
    setTimeout(function (){Show(out)}, 1);
}

const SaveComments = () => {
    localStorage.setItem('comments', JSON.stringify(comments));
}

const LoadComments = () => {
    if (localStorage.getItem('comments')) comments = JSON.parse(localStorage.getItem('comments'));
    ShowComments();
}

const TimeConverter = (UNIX_timestamp) => {
    let a = new Date(UNIX_timestamp * 1000);
    let months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
    let year = a.getFullYear();
    let month = months[a.getMonth()];
    let date = a.getDate();
    let hour = a.getHours();
    let min = a.getMinutes();
    let sec = a.getSeconds();
    let time = date + ' ' + month + ' ' + year + ' ' + hour + ':' + min + ':' + sec ;
    return time;
}

const Comments = (event) => {
    event.preventDefault();

    let comment = {
        name: $('#comment-name').val(),
        body: $('#comment-body').val(),
        time : Math.floor(Date.now() / 1000)
    }

    comments.push(comment);
    SaveComments();
    ShowComments();
}

const Reviews = () => {
    $('.active').removeClass('active');
    $('#reviews').addClass('active');
    LoadComments();

    return (
        <Main>
            <RevHead>Customer Reviews</RevHead>
            <RevForm>
                <FormName>
                    <label htmlFor={"comment-name"}>Name:</label>
                    <input type={"text"} id={"comment-name"} placeholder={"Enter your name"} />
                </FormName>
                <FormComment>
                    <label htmlFor={"comment-body"}>Comment:</label>
                    <textarea type={"text"} id={"comment-body"} placeholder={"comment"}></textarea>
                </FormComment>
                <FormBtn>
                    <button type={"submit"} id={"comment-add"} onClick={Comments}>add Comment</button>
                </FormBtn>
            </RevForm>
            <RevComments id={"comment-field"}></RevComments>
        </Main>
    );
};

export default Reviews;
