import React, {Fragment, useState, useRef, useEffect} from 'react';
import {Button, Card} from "./index";
import styled from "styled-components";

const AddFormContent = styled.div`
  padding: 0 12px 12px 12px;
`

const AddFormInput = styled.div`
  textarea{
    border: 0;
    padding: 0;
    margin: 0;
    width: 100%;
    resize: none;
    outline: none;
  }
`

const AddFormBottom = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`

const AddFormClear = styled.img`
  cursor: pointer;
`

const PanelBottom = styled.div`


  :hover{
    background-color: rgba(0,0,0,0.1);
  }
`

const PanelButton = styled.div`
  border-radius: 0 0 3px 3px;
  padding: 12px;
  display: flex;
  align-items: center;
  cursor: pointer;
  color: #6B808C;

  :hover {
    color: #323b41;
  }

  img {
    margin-right: 8px;
  }
`

const AddForm = ({ children, onAddCard, onAddPanel, isEmptyPanel }) => {
    const [ showForm, setShowForm ] = useState(false)
    const textareaRef = useRef(null);

    useEffect(() => {
        if (textareaRef.current){
            textareaRef.current.focus();
        }
    }, [showForm]);

    return (
        <Fragment>
            {showForm ? (
                <AddFormContent className={'addForm'}>
                    <AddFormInput>
                        <Card>
                            <textarea ref={textareaRef} rows={'3'} placeholder={isEmptyPanel ? 'Введите название колонки' : 'Введите название карточки'} />
                        </Card>
                        <AddFormBottom>
                            <Button onClick={isEmptyPanel ? onAddPanel : onAddCard}>{isEmptyPanel ? 'Добавить колонку' : 'Добавить карточку'}</Button>
                            <AddFormClear onClick={setShowForm.bind(this, false)} src={'./img/clear.svg'} alt={'clear'}/>
                        </AddFormBottom>
                    </AddFormInput>
                </AddFormContent>
                ) : (
                    <PanelBottom>
                        <PanelButton onClick={setShowForm.bind(this, true)}>
                            <img src={'./img/add.svg'} alt={'Add icon'} />
                            <span>{isEmptyPanel ? 'Добавить ещё одну колонку' : 'Добавить ещё одну карточку'}</span>
                        </PanelButton>
                    </PanelBottom>
            )}
        </Fragment>
    );
};

export default AddForm;
