import React, {Fragment} from 'react';
import {Button, Card} from "./index";
import styled from "styled-components";

const AddFormContent = styled.div`
  background: #FFFFFF;
  box-shadow: 0px 1px 4px rgba(9, 45, 66, 0.25);
  border-radius: 3px;
  padding: 12px;
`

const AddFormInput = styled.div`
  textarea{
    border: 0;
    padding: 0;
    margin: 0;
    width: 100%;
    resize: none;
  }
`

const AddFormBottom = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`

const AddForm = ({ children }) => {
    return (
        <Fragment>
            <AddFormContent>
                <AddFormInput>
                    <textarea rows={'3'} />
                    <AddFormBottom>
                        <Button>Добавить карточку</Button>
                        <img src={'./img/clear.svg'} alt={'clear'} />
                    </AddFormBottom>
                </AddFormInput>
            </AddFormContent>
        </Fragment>
    );
};

export default AddForm;
