import React, {Fragment} from 'react';
import styled from "styled-components";
import PropTypes from 'prop-types';
import { Card, AddForm } from "./index";
import classNames from 'classnames';

const ColumnContent = styled.div`
  background: #DFE3E6;
  border-radius: 3px;
  width: 272px;
  margin-right: 12px;
  overflow: hidden;
`

const ColumnItems = styled.div`
  padding: 12px 12px 0 12px;
`

const ColumnTitle = styled.div`
  padding: 12px 12px 0 12px;
`

const Column = ({ columnIndex, title, cards, onAddColumn, onAddCard }) => {
    return (
        <ColumnContent className={classNames('', {'column-empty': !cards})}>
            {title && <ColumnTitle><b>{title}</b></ColumnTitle>}
            {cards && (
                <ColumnItems>
                {cards.map((card, index) => (
                    <Card key={index}>{card}</Card>
                ))}
                </ColumnItems>
            )}
            <AddForm
                columnIndex={columnIndex}
                isEmptyColumn={!cards}
                onAddColumn={onAddColumn}
                onAddCard={onAddCard}
            />
        </ColumnContent>
    );
};

Column.propTypes = {
    cards: PropTypes.node,
    title: PropTypes.string
};

export default Column;
