import React from 'react';
import styled from "styled-components";
import $ from "jquery";

const Main = styled.div`
`

let comments = [];

const Reviews = () => {
    $('.active').removeClass('active');
    $('#reviews').addClass('active');
    loadComments();

    document.getElementById('comment-add').onclick = function(){
        let commentName = document.getElementById('comment-name');
        let commentBody = document.getElementById('comment-body');

        let comment = {
            name : commentName.value,
            body : commentBody.value,
            time : Math.floor(Date.now() / 1000)
        }

        commentName.value = '';
        commentBody.value = '';

        comments.push(comment);
        saveComments();
        showComments();
    }

    function saveComments(){
        localStorage.setItem('comments', JSON.stringify(comments));
    }

    function loadComments(){
        if (localStorage.getItem('comments')) comments = JSON.parse(localStorage.getItem('comments'));
        showComments();
    }

    function showComments (){
        let commentField = document.getElementById('comment-field');
        let out = '';
        comments.forEach(function(item){
            out += `<p class="text-right small"><em>${timeConverter(item.time)}</em></p>`;
            out += `<p class="alert alert-primary" role="alert">${item.name}</p>`;
            out += `<p class="alert alert-success" role="alert">${item.body}</p>`;
        });
        commentField.innerHTML = out;
    }

    function timeConverter(UNIX_timestamp){
        var a = new Date(UNIX_timestamp * 1000);
        var months = ['Jan','Feb','Mar','Apr','May','Jun','Jul','Aug','Sep','Oct','Nov','Dec'];
        var year = a.getFullYear();
        var month = months[a.getMonth()];
        var date = a.getDate();
        var hour = a.getHours();
        var min = a.getMinutes();
        var sec = a.getSeconds();
        var time = date + ' ' + month + ' ' + year + ' ' + hour + ':' + min + ':' + sec ;
        return time;
    }
    return (
        <Main>
            <h2 className="text-center">
                Comment
            </h2>
            <div id="comment-field"></div>
            <form>
                <div className="form-group">
                    <label htmlFor="comment-name">Name:</label>
                    <input type="email" className="form-control" id="comment-name" placeholder="Enter your name" />
                </div>
                <div className="form-group">
                    <label htmlFor="comment-body">Comment:</label>
                    <textarea type="password" className="form-control" id="comment-body" placeholder="comment"></textarea>
                </div>
                <div className="form-group form-check text-right">
                    <button type="submit" id="comment-add" className="btn btn-primary">add Comment</button>
                </div>
            </form>
        </Main>
    );
};

export default Reviews;
