import React from 'react';
import {Button, Card} from "./index";
import styled from "styled-components";

const AddFormContent = styled.div`
  padding: 0 12px 12px 12px;
`

const AddFormInput = styled.div`
  textarea{
    border: 0;
    padding: 0;
    margin: 0;
    width: 100%;
    resize: none;
    outline: none;
  }
`

const AddFormBottom = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`

const AddFormClear = styled.img`
  cursor: pointer;
`

const ColumnBottom = styled.div`


  :hover{
    background-color: rgba(0,0,0,0.1);
  }
`

const ColumnButton = styled.div`
  border-radius: 0 0 3px 3px;
  padding: 12px;
  display: flex;
  align-items: center;
  cursor: pointer;
  color: #6B808C;

  :hover {
    color: #323b41;
  }

  img {
    margin-right: 8px;
  }
`

const AddForm = ({
                     value,
                     showForm,
                     setValue,
                     columnIndex,
                     textareaRef,
                     setShowForm,
                     children,
                     onAdd,
                     isEmptyColumn
                 }) => {
    return showForm ? (
        <AddFormContent>
            <AddFormInput>
                <Card>
                    <textarea
                        onChange={e => setValue(e.target.value)}
                        value={value}
                        placeholder={
                            isEmptyColumn
                                ? "Введите название колонку"
                                : "Введите название карточку"
                        }
                        ref={textareaRef}
                        rows="3"
                    />
                </Card>
                <AddFormBottom>
                    <Button onClick={onAdd}>
                        {isEmptyColumn ? "Добавить колонку" : "Добавить карточку"}
                    </Button>
                    <AddFormClear
                        onClick={setShowForm.bind(this, false)}
                        src='./img/clear.svg'
                        alt="Clear svg icon"
                    />
                </AddFormBottom>
            </AddFormInput>
        </AddFormContent>
    ) : (
        <ColumnBottom className="add-form__bottom">
            {/*<ColumnButton onClick={setShowForm.bind(this, true)}>*/}
            <ColumnButton>
                <img src={'./img/add.svg'} alt="Add svg icon" />
                <span>
                    {isEmptyColumn
                    ? "Добавить еще одну колонку"
                    : "Добавить еще одну карточку"}
                </span>
            </ColumnButton>
        </ColumnBottom>
    );
};

export default AddForm;
