import React from 'react';
import styled from "styled-components";
import PropTypes from 'prop-types';
import { Card, AddForm } from "./index";
import { Droppable } from "react-beautiful-dnd";

const ColumnContent = styled.div`
  width: 272px;
  margin: 0 4px;
  height: calc(100% - 40px);
  box-sizing: border-box;
  display: inline-block;
  vertical-align: top;
  white-space: nowrap;
`

const ColumnInner = styled.div`
  //background-color: #dfe1e6;
  background-color: rgba(255,255,255,0.1);
  border-radius: 3px;
  box-sizing: border-box;
  display: flex;
  flex-direction: column;
  max-height: 100%;
  position: relative;
  white-space: normal;
  overflow: hidden;
`

const ColumnItems = styled.div`
  //padding: 0 12px 0 12px;
  padding: 12px;
  overflow-y: auto;
  overflow-x: hidden;
`

const ColumnTitle = styled.div`
  //display: flex;
  //align-items: center;
  //justify-content: space-between;
  padding: 12px;
  text-align: center;
  color: #fff;
`

const ColumnRemove = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 3px;
  width: 24px;
  height: 24px;
  cursor: pointer;

  img{
    width: 12px;
  }
  :hover{
    background-color: rgba(9, 30, 66, 0.13);
  }
`

const Column = ({ columnIndex, title, cards, onAddColumn, onAddCard, onRemove, onReorder }) => {
    const removeColumn = () => {
        if (global.confirm('Вы действительно хотите удалить колонку?')) {
            onRemove(columnIndex);
        }
    };

    return cards ? (
        <Droppable type="CARDS" droppableId={`column-${columnIndex}`}>
            {provided => (
                <ColumnContent {...provided.droppableProps} ref={provided.innerRef}>
                    <ColumnInner>
                        {title && (
                            <ColumnTitle>
                                <b>{title}</b>
                                {/*<ColumnRemove onClick={removeColumn}>*/}
                                {/*    <img src={'./img/clear.svg'} alt="Clear svg icon" />*/}
                                {/*</ColumnRemove>*/}
                            </ColumnTitle>
                        )}
                        <ColumnItems>
                            {cards.map((card, index) => (
                                <Card key={index} columnIndex={columnIndex} cardIndex={index}>
                                    {card}
                                </Card>
                            ))}
                            {provided.placeholder}
                        </ColumnItems>
                        {/*<AddForm*/}
                        {/*    isEmptyColumn={false}*/}
                        {/*    columnIndex={columnIndex}*/}
                        {/*    onAddColumn={onAddColumn}*/}
                        {/*    onAddCard={onAddCard}*/}
                        {/*/>*/}
                    </ColumnInner>
                </ColumnContent>
            )}
        </Droppable>
    ) : (
        <ColumnContent className={"column--empty"}>
            <ColumnInner>
                <AddForm
                    isEmptyColumn={true}
                    columnIndex={columnIndex}
                    onAddColumn={onAddColumn}
                    onAddCard={onAddCard}
                />
            </ColumnInner>
        </ColumnContent>
    );
};

Column.propTypes = {
    cards: PropTypes.node,
    title: PropTypes.string
};

export default Column;
