import styled from "styled-components";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import "./FontAwesome";
import '../fonts/stylesheet.css';

const Row = styled.div`
  display: flex;
  align-items: center;

  #menu-bar{
    display: none;
    color: #fff;
  }

  @media (max-width: 768px){


    #menu-bar{
      display: block;
      font-size: 50px;
    }
  }
`
const Head = styled.header`
  position: absolute;
  left: 0;
  top: 0;
  width: 100%;
  padding: 15px 0;
  border-bottom: 1px solid rgba(255, 255, 255, 0.3);
`
const Logo = styled.div`
  width: 30%;
  text-align: center;

  img{
    cursor: pointer;
  }

  @media (max-width: 991px){
    width: 15%;
  }
  @media (max-width: 768px){
    width: 70%;
    text-align: left;
  }
`
const MainMenu = styled.div`
  width: 50%;

  ul{
    display: flex;
    list-style: none;
    justify-content: space-evenly;
  }
  ul li a{
    color: #fff;
    text-decoration: none;
    cursor: pointer;
  }
  ul li:hover a{
    border-bottom: 5px solid #11749e;
    color: #1698D0;
    padding-bottom: 40px;
  }
  ul li a.active{
    border-bottom: 5px solid #11749e;
    padding-bottom: 40px;
  }

  @media (max-width: 991px){
    width: 70%;
  }
  @media (max-width: 768px){
    display: none;
  }
`
const Contacts = styled.div`
  width: 20%;

  svg{
    margin: 0 8px;
    cursor: pointer;
    color: #fff;
  }
  svg:hover{
    color: #1698D0;
  }

  @media (max-width: 991px){
    width: 15%;

    svg{
      margin: 0 5px;
    }
  }
  @media (max-width: 768px){
    display: none;
  }
`

const Header = () => {
    return(
        <Head>
            <div className='container'>
                <Row>
                    <Logo>
                        <a href='/'><img src='./img/logo.png' alt='CS' /></a>
                    </Logo>
                    <MainMenu>
                        <ul>
                            <li><a href='/' className='active'>Home</a></li>
                            <li><a href='/'>Pages</a></li>
                            <li><a href='/'>Features</a></li>
                            <li><a href='/'>Extensions</a></li>
                            <li><a href='/'>Tutorials</a></li>
                            <li><a href='/'>Contact us</a></li>
                        </ul>
                    </MainMenu>
                    <Contacts>
                        <a href={'https://facebook.com'} target={'_blank'}><FontAwesomeIcon icon={['fab', 'facebook']} /></a>
                        <a href={'https://twitter.com'} target={'_blank'}><FontAwesomeIcon icon={['fab', 'twitter']} /></a>
                        <a href={'https://skype.com'} target={'_blank'}><FontAwesomeIcon icon={['fab', 'skype']} /></a>
                        <a href={'https://instagram.com'} target={'_blank'}><FontAwesomeIcon icon={['fab', 'instagram']} /></a>
                    </Contacts>
                    <FontAwesomeIcon id={'menu-bar'} icon={['fas', 'bars']} />
                </Row>
            </div>
        </Head>
    );
}

export default Header;
