import styled from "styled-components";

const BgContact = styled.section`
  text-align: center;
  background-image: url("./img/Contact.png");
  background-size: cover;
  position: relative;
  padding-top: 100px;
`

const Heading = styled.h2`
  text-transform: uppercase;
  font-family: 'Gotham';
  font-weight: 500;
  font-size: 38px;
  color: #fff;
  margin: 50px 0 150px 0;
`

const C = styled.span`
  position: absolute;
  top: 0;
  left: 50%;
  transform: translateX(-50%);
  font-family: 'robotobold';
  font-size: 250px;
  color: #fff;
  opacity: 0.18;
`

const ContactData = styled.div`
  display: flex;

  input{
    width: 100%;
    font-family: 'Gotham';
    font-weight: normal;
    font-size: 15px;
    padding: 15px;
    border: 1px solid #fff;
    background: rgba(0,0,0,0);
    color: #fff;
  }

  input:nth-child(2){
    margin: 0 25px;
  }

  input::placeholder{
    color: #fff;
  }
`

const ContactMessage = styled.div`
  margin: 40px 0 50px 0;

  textarea{
    width: 100%;
    height: 150px;
    font-family: 'Gotham';
    font-weight: normal;
    font-size: 15px;
    padding: 15px;
    border: 1px solid #fff;
    background: rgba(0,0,0,0);
    color: #fff;
  }

  textarea::placeholder{
    color: #fff;
  }
`

const ContactButton = styled.div`
  margin-bottom: 100px;

  button{
    font-family: 'Gotham';
    font-weight: 500;
    font-size: 24px;
    color: #fff;
    background: #11749e;
    border: none;
    padding: 15px 60px;
  }
`

const Map = styled.div`
  position: relative;
  overflow: hidden;

  iframe{
    position: relative;
    width: 100%;
    height: 330px;
    border: none;
    -ms-filter: grayscale(1);
    -webkit-filter: grayscale(1);
    -moz-filter: grayscale(1);
    -o-filter: grayscale(1);
    filter: grayscale(1);
  }
`

const Contact = () => {
    return(
        <BgContact>
            <div className={'container'}>
                <Heading>Weâ€™d love to hear about your project.</Heading>
                <C>C</C>
                <form>
                    <ContactData>
                        <input type={'text'} placeholder={'Name'} required />
                        <input type={'email'} placeholder={'Email'} required />
                        <input type={'tel'} placeholder={'Phone'} required />
                    </ContactData>
                    <ContactMessage>
                        <textarea placeholder={'Message'} required></textarea>
                    </ContactMessage>
                    <ContactButton>
                        <button type={'submit'}>SUBMIT</button>
                    </ContactButton>
                </form>
            </div>
            <Map>
                <iframe src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d2436.246386806753!2d30.40110381568545!3d52.365952255386745!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x46d43071178bebeb%3A0x7b2d6a35b81fc5d8!2z0YPQuy4g0KHQvtCy0LXRgtGB0LrQsNGPIDk3LCDQoNC10YfQuNGG0LA!5e0!3m2!1sru!2sby!4v1633687839502!5m2!1sru!2sby"
                    allowFullScreen="" loading="lazy"></iframe>
            </Map>
        </BgContact>
    );
}

export default Contact;
