import styled from "styled-components";
import {FontAwesomeIcon} from "@fortawesome/react-fontawesome";
import './FontAwesome';
import '../fonts/stylesheet.css';

const AboutBg = styled.section`
  background: #11749e;
  position: relative;
  text-align: center;
`

const Heading = styled.h2`
  text-transform: uppercase;
  font-family: 'Gotham';
  font-weight: 500;
  font-size: 38px;
  color: #fff;
  padding: 150px 0 100px 0;
`
const W = styled.span`
  position: absolute;
  top: 0;
  left: 50%;
  transform: translateX(-50%);
  font-family: 'robotobold';
  font-size: 250px;
  color: #fff;
  opacity: 0.18;
`

const Feature = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-around;
  margin-top: 30px;
`

const Circle = styled.div`
  background: #2e85aa;
  height: 135px;
  width: 135px;
  border-radius: 100%;
  position: relative;

  svg{
    color: #fff;
    font-size: 35px;
    position: absolute;
    top: 50%;
    left: 50%;
    transform: translate(-45%, -55%);
  }
`

const About = () =>{
    return(
        <AboutBg>
            <div className='container'>
                <Heading>We Are An Awesome Agency</Heading>
                <W>W</W>
                <Feature>
                    <Circle>
                        <FontAwesomeIcon icon={['far', 'edit']} />
                    </Circle>
                    <Circle>

                    </Circle>
                    <Circle>

                    </Circle>
                    <Circle>

                    </Circle>
                </Feature>
            </div>
        </AboutBg>
    );
}

export default About;
